import React, { useState } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import GreenLeaf from '../images/leaf-green.png';
import LeafShadow from '../images/leaf-shadow.png';

let myIcon = L.icon({
  iconUrl :'data:image/png;base64,',
  iconSize : [25,41],
  iconAnchor : [12,5,41],
  popupAnchor: [4, -41]
})

const MyMap = (props) => {

  const coordinates = {
    lat: 40.404306399999996,
    lng: -3.6521948,
    zoom: 7,
    
    greenIcon:{
      lat: 40.7516 ,
      lng: -3.8859
    }  
  }

  const [markerPosition, setMarkerPosition] = useState({
    lat: 49.8419,
    lng: 24.0315
    });

  function moveMarker() {
      setMarkerPosition({
        lat: props.latitude,
        lng: props.longitude
      });
    }

  const greenIcon = L.icon({
    iconUrl : GreenLeaf,
    shadowUrl : LeafShadow,
    iconSize:     [38, 95], 
    shadowSize:   [50, 64], 
    iconAnchor:   [22, 94], 
    shadowAnchor: [4, 62],  
    popupAnchor:  [-3, -76] 
  })

  const position = [props.latitude, props.longitude];

  return (
    <div className="map-area">
      <button type="button" className ="button" onClick={moveMarker}>Locate yourself</button>    
      <MapContainer className = "map" center={position} zoom={coordinates.zoom} >
        <TileLayer attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors' url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
        <Marker position={markerPosition} icon={myIcon} className = "myposition"></Marker>
        {props.markers.map((myPosition, idx) => 
          <Marker key={`marker-${idx}`} position={myPosition.itemCoor} icon={greenIcon}>
            <Popup>
              <a href={myPosition.itemUrl}>{myPosition.itemName}</a>
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  ) 
}

export default MyMap;